mapper.insert:
INSERT
  INTO TBL_EMP_INFO A
 (
   A.EMP_NO
 , A.EMP_NAME  
 , A.EMP_ID  
 , A.EMP_PWD  
 , A.EMP_EMAIL  
 , A.EMP_GENDER  
 , A.EMP_BIRTH  
 , A.AUTHORITY  
 , A.HIRE_DATE  
 , A.EMP_PHONE  
 , A.JOB_CODE  
 , A.DEPT_CODE  
 , A.MANAGER_NO  
 ) 
  VALUES
 (
   SEQ_EMP_NO.NEXTVAL
 , #{empName}
 , #{empId}
 , #{empPwd}
 , #{empEmail}
 , #{empGender}
 , #{empBirth}  
 , #{authority}  
 , #{hireDate}  
 , #{empPhone}  
 , #{jobCode}  
 , #{deptCode}  
 , #{empNo}  
 )
