mapper.insert:
INSERT
  INTO TBL_EMP_INFO A
 (
   A.EMP_NO
 , A.EMP_NAME  
 , A.EMP_ID  
 , A.EMP_PWD  
 , A.EMP_EMAIL  
 , A.EMP_GENDER  
 , A.EMP_BIRTH  
 , A.AUTHORITY  
 , A.HIRE_DATE  
 , A.EMP_PHONE  
 , A.JOB_CODE  
 , A.DEPT_CODE  
 , A.MANAGER_NO
 , A.EMP_TEL
 ) 
  VALUES
 (
   SEQ_EMP_NO.NEXTVAL
 , #{empName}
 , #{empId}
 , #{empPwd}
 , #{empEmail}
 , #{empGender}
 , #{empBirth}  
 , #{authority}  
 , #{hireDate}  
 , #{empPhone}  
 , #{jobCode}  
 , #{deptCode}  
 , #{managerNo} 
 , #{empTel}  
 )

        INSERT
		  INTO TBL_EMP_IMAGE A
		 (
		   A.EMP_IMG_NO
		 , A.EMP_NO
		 , A.ORIGINAL_NAME
		 , A.SAVED_NAME
		 , A.SAVED_PATH
		 , A.THUMBNAIL_PATH
		 ) 
		  VALUES
		 (
		   SEQ_EMP_IMG_NO.NEXTVAL
		 , SEQ_EMP_NO.CURRVAL
		 , #{originalName}  
		 , #{savedName}  
		 , #{savedPath}  
		 , #{thumbnailPath}  
		 )
