mapper.delete:
DELETE /*com.deft.crud.board.model.dto.BoardDTO*/
  FROM TBL_BOARD B
 WHERE B.WRITE_NO = #{ writeNo, jdbcType = INTEGER }

	DELETE /* com.deft.crud.board.model.dto.BoardFileDTO */
      FROM TBL_BOARD_ATTATCH B
     WHERE B.WRITE_NO = #{ writeNo, jdbcType = INTEGER }

