mapper.insert:
INSERT
  INTO TBL_DEPARTMENT D
     (
       D.DEPT_CODE
     , D.DEPT_NAME  
     , D.DEPT_FAX
     , D.DEPT_TEL
     , D.DEPT_STATUS
     )
VALUES
     (
        ('D' || TO_CHAR(SEQ_DEPT_CODE.NEXTVAL))
     , #{ deptName }  
     , #{ deptFax }
     , #{ deptTel }
     , #{ deptStatus }
     )

	INSERT
	  INTO TBL_JOB J
	     (
	       J.JOB_CODE
	     , J.JOB_NAME
	     , J.JOB_STATUS
	     )
	VALUES    
         (
          ('J' || TO_CHAR(SEQ_JOB_CODE.NEXTVAL))
         , #{ jobName }
         , #{ jobStatus }
         )

mapper.update:
	UPDATE 
	       TBL_DEPARTMENT D
       SET D.DEPT_NAME = #{ deptName }
         , D.DEPT_FAX = #{ deptFax }
         , D.DEPT_TEL = #{ deptTel}
         , D.DEPT_STATUS = #{ deptStatus }
     WHRER D.DEPT_CODE = #{ deptCode }

	UPDATE 
	       TBL_JOB J
       SET J.JOB_NAME = #{ jobName }
         , J.JOB_STATUS = #{ jobStatus }
     WHRER J.JOB_CODE = #{ jobCode }

	UPDATE 
	       TBL_DEPARTMENT D
       SET D.DEPT_STATUS = #{ deptStatus }
      WHRER D.DEPT_CODE = #{ deptCode }

	UPDATE 
	       TBL_JOB J
       SET J.JOB_STATUS = #{ jobStatus }
     WHRER J.JOB_CODE = #{ jobCode }
